import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_ENDPOINTS } from '../../config/api';
import toast from 'react-hot-toast';
import { 
  User, 
  Clock, 
  Calendar, 
  BookOpen, 
  Percent, 
  Award, 
  ArrowLeft,
  Mail,
  Phone,
  Hash,
  Edit3,
  Save,
  X
} from 'lucide-react';

const StudentProfile = () => {
  const { user, updateProfile } = useAuth();
  const navigate = useNavigate();
  const [testHistory, setTestHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    name: '',
    phone: ''
  });
  const [stats, setStats] = useState({
    total: 0,
    completed: 0,
    toAttempt: 0,
    missed: 0,
    avgScore: 0
  });

  useEffect(() => {
    if (user) {
      setEditForm({
        name: user.name || '',
        phone: user.phone || ''
      });
      fetchTestHistory();
    }
  }, [user]);

  const fetchTestHistory = async () => {
    if (!user?._id) {
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get(API_ENDPOINTS.STUDENT_TEST_HISTORY(user._id));
      const tests = response.data.tests || [];
      setTestHistory(tests);
      
      // Calculate stats
      const completed = tests.filter(t => t.status === 'completed').length;
      const totalScore = tests.reduce((sum, t) => sum + (t.score || 0), 0);
      const avgScore = completed > 0 ? Math.round(totalScore / completed) : 0;
      
      setStats({
        total: tests.length,
        completed,
        toAttempt: tests.filter(t => t.status === 'to_attempt').length,
        missed: tests.filter(t => t.status === 'missed').length,
        avgScore
      });
    } catch (error) {
      console.error('Error fetching test history:', error);
      // Don't show error toast if it's just a 404 (no history)
      if (error.response?.status !== 404) {
        toast.error('Failed to load test history');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleEditToggle = () => {
    if (isEditing) {
      // Reset form if canceling
      setEditForm({
        name: user.name || '',
        phone: user.phone || ''
      });
    }
    setIsEditing(!isEditing);
  };

  const handleSaveProfile = async () => {
    try {
      const result = await updateProfile(editForm);
      if (result.success) {
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const handleInputChange = (e) => {
    setEditForm({
      ...editForm,
      [e.target.name]: e.target.value
    });
  };

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="p-6">
      <div className="max-w-4xl mx-auto">
        {/* Profile Header */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex items-center space-x-4">
            <div className="bg-indigo-100 p-3 rounded-full">
              <User className="w-6 h-6 text-indigo-600" />
            </div>
            <div>
              <h2 className="text-xl font-semibold text-gray-900">{user?.name}</h2>
              <p className="text-gray-600">{user?.email}</p>
            </div>
          </div>
        </div>

        {/* Test History */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Test History</h3>
          {testHistory.length === 0 ? (
            <p className="text-gray-600">No test history available.</p>
          ) : (
            <div className="space-y-4">
              {testHistory.map((test) => (
                <div key={test._id} className="border border-gray-100 rounded-lg p-4">
                  <div className="flex justify-between items-start mb-2">
                    <h4 className="font-medium text-gray-900">{test.title}</h4>
                    <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                      test.score >= 70 ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      Score: {test.score || 0}%
                    </span>
                  </div>
                  <div className="grid grid-cols-2 gap-4 text-sm text-gray-600">
                    <div className="flex items-center">
                      <Calendar className="w-4 h-4 mr-2" />
                      <span>Completed: {new Date(test.completedAt).toLocaleDateString()}</span>
                    </div>
                    <div className="flex items-center">
                      <Clock className="w-4 h-4 mr-2" />
                      <span>Duration: {test.duration} min</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default StudentProfile;
