import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { API_ENDPOINTS } from '../../config/api';
import { useAuth } from '../../contexts/AuthContext';
import { 
  Plus, 
  Trash2, 
  Upload, 
  Save, 
  ArrowLeft,
  Clock,
  FileText,
  Image as ImageIcon
} from 'lucide-react';

const CreateTest = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [testData, setTestData] = useState({
    title: '',
    description: '',
    startDate: '',
    endDate: '',
    duration: 60,
    enableCamera: true,
    enableFullScreen: true,
    preventCopyPaste: true,
    preventRightClick: true,
    sections: []
  });
  const [loading, setLoading] = useState(false);

  // Check if user is admin
  useEffect(() => {
    if (user && user.role !== 'admin') {
      toast.error('Access denied. Admin only.');
      navigate('/');
    }
  }, [user, navigate]);

  // Show loading if user data is not loaded yet
  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  const sectionTypes = [
    'Aptitude',
    'Technical',
    'Coding',
    'Logical Reasoning',
    'English',
    'Domain Specific'
  ];

  const questionTypes = [
    { value: 'single-correct', label: 'Single Correct Answer' },
    { value: 'multi-correct', label: 'Multiple Correct Answers' },
    { value: 'coding', label: 'Coding Problem' }
  ];

  const addSection = () => {
    const newSection = {
      name: 'Aptitude',
      timeLimit: 30,
      numberOfQuestions: 10,
      instructions: '',
      order: testData.sections.length,
      questions: []
    };
    
    setTestData(prev => ({
      ...prev,
      sections: [...prev.sections, newSection]
    }));
  };

  const updateSection = (sectionIndex, field, value) => {
    setTestData(prev => ({
      ...prev,
      sections: prev.sections.map((section, index) => 
        index === sectionIndex ? { ...section, [field]: value } : section
      )
    }));
  };

  const removeSection = (sectionIndex) => {
    setTestData(prev => ({
      ...prev,
      sections: prev.sections.filter((_, index) => index !== sectionIndex)
    }));
  };

  const addQuestion = (sectionIndex) => {
    const newQuestion = {
      questionText: 'Enter your question here',
      questionImage: '',
      questionType: 'single-correct',
      options: [
        { text: 'Option A', isCorrect: true },
        { text: 'Option B', isCorrect: false },
        { text: 'Option C', isCorrect: false },
        { text: 'Option D', isCorrect: false }
      ],
      codingDetails: {
        problemStatement: 'Enter problem statement here',
        inputFormat: 'Enter input format',
        outputFormat: 'Enter output format',
        examples: [{ input: 'Sample input', output: 'Sample output', explanation: 'Sample explanation' }],
        constraints: 'Enter constraints',
        testCases: [{ input: 'Test input', output: 'Expected output', isHidden: false }],
        timeLimit: 1000,
        memoryLimit: 256
      },
      points: 1,
      difficulty: 'medium'
    };

    setTestData(prev => ({
      ...prev,
      sections: prev.sections.map((section, index) => 
        index === sectionIndex 
          ? { ...section, questions: [...section.questions, newQuestion] }
          : section
      )
    }));
  };

  const updateQuestion = (sectionIndex, questionIndex, field, value) => {
    setTestData(prev => ({
      ...prev,
      sections: prev.sections.map((section, sIndex) => 
        sIndex === sectionIndex 
          ? {
              ...section,
              questions: section.questions.map((question, qIndex) =>
                qIndex === questionIndex ? { ...question, [field]: value } : question
              )
            }
          : section
      )
    }));
  };

  const updateQuestionOption = (sectionIndex, questionIndex, optionIndex, field, value) => {
    setTestData(prev => ({
      ...prev,
      sections: prev.sections.map((section, sIndex) => 
        sIndex === sectionIndex 
          ? {
              ...section,
              questions: section.questions.map((question, qIndex) =>
                qIndex === questionIndex 
                  ? {
                      ...question,
                      options: question.options.map((option, oIndex) =>
                        oIndex === optionIndex ? { ...option, [field]: value } : option
                      )
                    }
                  : question
              )
            }
          : section
      )
    }));
  };

  const removeQuestion = (sectionIndex, questionIndex) => {
    setTestData(prev => ({
      ...prev,
      sections: prev.sections.map((section, sIndex) => 
        sIndex === sectionIndex 
          ? {
              ...section,
              questions: section.questions.filter((_, qIndex) => qIndex !== questionIndex)
            }
          : section
      )
    }));
  };

  const uploadImage = async (file, sectionIndex, questionIndex) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post(API_ENDPOINTS.ADMIN_UPLOAD_IMAGE, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      updateQuestion(sectionIndex, questionIndex, 'questionImage', response.data.imageUrl);
      toast.success('Image uploaded successfully');
    } catch (error) {
      console.error('Error uploading image:', error);
      toast.error('Failed to upload image');
    }
  };

  const addExample = (sectionIndex, questionIndex) => {
    const question = testData.sections[sectionIndex].questions[questionIndex];
    const newExamples = [...question.codingDetails.examples, { input: '', output: '', explanation: '' }];
    
    updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
      ...question.codingDetails,
      examples: newExamples
    });
  };

  const addTestCase = (sectionIndex, questionIndex) => {
    const question = testData.sections[sectionIndex].questions[questionIndex];
    const newTestCases = [...question.codingDetails.testCases, { input: '', output: '', isHidden: false }];
    
    updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
      ...question.codingDetails,
      testCases: newTestCases
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (testData.sections.length === 0) {
      toast.error('Please add at least one section');
      return;
    }

    // Validate sections have questions
    for (let section of testData.sections) {
      if (section.questions.length === 0) {
        toast.error(`Section "${section.name}" must have at least one question`);
        return;
      }
      
      // Validate each question
      for (let question of section.questions) {
        if (!question.questionText || question.questionText.trim() === '') {
          toast.error(`Question in section "${section.name}" must have text`);
          return;
        }
        
        // Validate MCQ questions have at least one correct option
        if (question.questionType !== 'coding') {
          const hasCorrectOption = question.options.some(option => option.isCorrect);
          if (!hasCorrectOption) {
            toast.error(`Question "${question.questionText}" must have at least one correct option`);
            return;
          }
          
          // Validate option text is not empty
          for (let option of question.options) {
            if (!option.text || option.text.trim() === '') {
              toast.error(`All options for question "${question.questionText}" must have text`);
              return;
            }
          }
        }
      }
    }

    // Validate required fields
    if (!testData.title || !testData.startDate || !testData.endDate || !testData.duration) {
      toast.error('Please fill in all required fields');
      return;
    }

    // Log the data being sent for debugging
    console.log('Sending test data:', testData);

    // Clean up the data before sending (remove empty strings, ensure proper types)
    const cleanTestData = {
      ...testData,
      title: testData.title.trim(),
      description: testData.description.trim(),
      duration: parseInt(testData.duration),
      startDate: testData.startDate,
      endDate: testData.endDate,
      sections: testData.sections.map(section => ({
        ...section,
        name: section.name.trim(),
        timeLimit: parseInt(section.timeLimit),
        numberOfQuestions: parseInt(section.numberOfQuestions),
        order: parseInt(section.order),
        instructions: section.instructions.trim(),
        questions: section.questions.map(question => ({
          ...question,
          questionText: question.questionText.trim(),
          points: parseInt(question.points),
          options: question.questionType !== 'coding' ? question.options.map(option => ({
            ...option,
            text: option.text.trim()
          })) : [],
          codingDetails: question.questionType === 'coding' ? {
            ...question.codingDetails,
            problemStatement: question.codingDetails.problemStatement.trim(),
            inputFormat: question.codingDetails.inputFormat.trim(),
            outputFormat: question.codingDetails.outputFormat.trim(),
            constraints: question.codingDetails.constraints.trim(),
            examples: question.codingDetails.examples.map(example => ({
              ...example,
              input: example.input.trim(),
              output: example.output.trim(),
              explanation: example.explanation.trim()
            })),
            testCases: question.codingDetails.testCases.map(testCase => ({
              ...testCase,
              input: testCase.input.trim(),
              output: testCase.output.trim()
            }))
          } : undefined
        }))
      }))
    };

    console.log('Cleaned test data:', cleanTestData);

    setLoading(true);
    try {
      const response = await axios.post(API_ENDPOINTS.ADMIN_TESTS, cleanTestData);
      toast.success('Test created successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error creating test:', error);
      if (error.response) {
        console.error('Error response:', error.response.data);
        toast.error(`Failed to create test: ${error.response.data.message || 'Unknown error'}`);
      } else {
        toast.error('Failed to create test: Network error');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/admin')}
                className="btn-secondary flex items-center space-x-2"
              >
                <ArrowLeft className="h-4 w-4" />
                <span>Back</span>
              </button>
              <h1 className="text-2xl font-bold text-gray-900">Create New Test</h1>
            </div>
            <button
              onClick={handleSubmit}
              disabled={loading}
              className="btn-primary flex items-center space-x-2"
            >
              <Save className="h-4 w-4" />
              <span>{loading ? 'Creating...' : 'Create Test'}</span>
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <form onSubmit={handleSubmit} className="space-y-8">
          {/* Basic Test Information */}
          <div className="card">
            <h2 className="text-xl font-semibold mb-6">Test Information</h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Test Title *
                </label>
                <input
                  type="text"
                  required
                  className="input-field"
                  value={testData.title}
                  onChange={(e) => setTestData(prev => ({ ...prev, title: e.target.value }))}
                  placeholder="Enter test title"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Duration (minutes) *
                </label>
                <input
                  type="number"
                  required
                  min="1"
                  className="input-field"
                  value={testData.duration}
                  onChange={(e) => setTestData(prev => ({ ...prev, duration: parseInt(e.target.value) }))}
                />
              </div>
            </div>

            <div className="mt-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                rows="3"
                className="input-field"
                value={testData.description}
                onChange={(e) => setTestData(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Enter test description"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start Date & Time *
                </label>
                <input
                  type="datetime-local"
                  required
                  className="input-field"
                  value={testData.startDate}
                  onChange={(e) => setTestData(prev => ({ ...prev, startDate: e.target.value }))}
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  End Date & Time *
                </label>
                <input
                  type="datetime-local"
                  required
                  className="input-field"
                  value={testData.endDate}
                  onChange={(e) => setTestData(prev => ({ ...prev, endDate: e.target.value }))}
                />
              </div>
            </div>

            {/* Proctoring Settings */}
            <div className="mt-6">
              <h3 className="text-lg font-medium mb-4">Proctoring Settings</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={testData.enableCamera}
                    onChange={(e) => setTestData(prev => ({ ...prev, enableCamera: e.target.checked }))}
                    className="rounded"
                  />
                  <span className="text-sm">Enable Camera</span>
                </label>
                
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={testData.enableFullScreen}
                    onChange={(e) => setTestData(prev => ({ ...prev, enableFullScreen: e.target.checked }))}
                    className="rounded"
                  />
                  <span className="text-sm">Full Screen</span>
                </label>
                
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={testData.preventCopyPaste}
                    onChange={(e) => setTestData(prev => ({ ...prev, preventCopyPaste: e.target.checked }))}
                    className="rounded"
                  />
                  <span className="text-sm">Prevent Copy/Paste</span>
                </label>
                
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={testData.preventRightClick}
                    onChange={(e) => setTestData(prev => ({ ...prev, preventRightClick: e.target.checked }))}
                    className="rounded"
                  />
                  <span className="text-sm">Prevent Right Click</span>
                </label>
              </div>
            </div>
          </div>

          {/* Sections */}
          <div className="card">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-semibold">Test Sections</h2>
              <button
                type="button"
                onClick={addSection}
                className="btn-primary flex items-center space-x-2"
              >
                <Plus className="h-4 w-4" />
                <span>Add Section</span>
              </button>
            </div>

            {testData.sections.length === 0 ? (
              <div className="text-center py-12 text-gray-500">
                <FileText className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                <p>No sections added yet. Click "Add Section" to get started.</p>
              </div>
            ) : (
              <div className="space-y-8">
                {testData.sections.map((section, sectionIndex) => (
                  <div key={sectionIndex} className="border border-gray-200 rounded-lg p-6">
                    <div className="flex justify-between items-center mb-4">
                      <h3 className="text-lg font-medium">Section {sectionIndex + 1}</h3>
                      <button
                        type="button"
                        onClick={() => removeSection(sectionIndex)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Section Type
                        </label>
                        <select
                          className="input-field"
                          value={section.name}
                          onChange={(e) => updateSection(sectionIndex, 'name', e.target.value)}
                        >
                          {sectionTypes.map(type => (
                            <option key={type} value={type}>{type}</option>
                          ))}
                        </select>
                      </div>
                      
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Time Limit (minutes)
                        </label>
                        <input
                          type="number"
                          min="1"
                          className="input-field"
                          value={section.timeLimit}
                          onChange={(e) => updateSection(sectionIndex, 'timeLimit', parseInt(e.target.value))}
                        />
                      </div>
                      
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Number of Questions
                        </label>
                        <input
                          type="number"
                          min="1"
                          className="input-field"
                          value={section.numberOfQuestions}
                          onChange={(e) => updateSection(sectionIndex, 'numberOfQuestions', parseInt(e.target.value))}
                        />
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Instructions
                      </label>
                      <textarea
                        rows="2"
                        className="input-field"
                        value={section.instructions}
                        onChange={(e) => updateSection(sectionIndex, 'instructions', e.target.value)}
                        placeholder="Enter section instructions"
                      />
                    </div>

                    {/* Questions */}
                    <div className="border-t pt-4">
                      <div className="flex justify-between items-center mb-4">
                        <h4 className="font-medium">Questions ({section.questions.length})</h4>
                        <button
                          type="button"
                          onClick={() => addQuestion(sectionIndex)}
                          className="btn-secondary flex items-center space-x-2"
                        >
                          <Plus className="h-4 w-4" />
                          <span>Add Question</span>
                        </button>
                      </div>

                      {section.questions.map((question, questionIndex) => (
                        <div key={questionIndex} className="bg-gray-50 rounded-lg p-4 mb-4">
                          <div className="flex justify-between items-center mb-4">
                            <h5 className="font-medium">Question {questionIndex + 1}</h5>
                            <button
                              type="button"
                              onClick={() => removeQuestion(sectionIndex, questionIndex)}
                              className="text-red-600 hover:text-red-800"
                            >
                              <Trash2 className="h-4 w-4" />
                            </button>
                          </div>

                          <div className="space-y-4">
                            <div>
                              <label className="block text-sm font-medium text-gray-700 mb-1">
                                Question Text *
                              </label>
                              <textarea
                                rows="3"
                                required
                                className="input-field"
                                value={question.questionText}
                                onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'questionText', e.target.value)}
                                placeholder="Enter question text"
                              />
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                  Question Type
                                </label>
                                <select
                                  className="input-field"
                                  value={question.questionType}
                                  onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'questionType', e.target.value)}
                                >
                                  {questionTypes.map(type => (
                                    <option key={type.value} value={type.value}>{type.label}</option>
                                  ))}
                                </select>
                              </div>
                              
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                  Points
                                </label>
                                <input
                                  type="number"
                                  min="1"
                                  className="input-field"
                                  value={question.points}
                                  onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'points', parseInt(e.target.value))}
                                />
                              </div>
                              
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                  Difficulty
                                </label>
                                <select
                                  className="input-field"
                                  value={question.difficulty}
                                  onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'difficulty', e.target.value)}
                                >
                                  <option value="easy">Easy</option>
                                  <option value="medium">Medium</option>
                                  <option value="hard">Hard</option>
                                </select>
                              </div>
                            </div>

                            {/* Image Upload */}
                            <div>
                              <label className="block text-sm font-medium text-gray-700 mb-1">
                                Question Image (Optional)
                              </label>
                              <div className="flex items-center space-x-4">
                                <input
                                  type="file"
                                  accept="image/*"
                                  onChange={(e) => {
                                    if (e.target.files[0]) {
                                      uploadImage(e.target.files[0], sectionIndex, questionIndex);
                                    }
                                  }}
                                  className="hidden"
                                  id={`image-${sectionIndex}-${questionIndex}`}
                                />
                                <label
                                  htmlFor={`image-${sectionIndex}-${questionIndex}`}
                                  className="btn-secondary flex items-center space-x-2 cursor-pointer"
                                >
                                  <Upload className="h-4 w-4" />
                                  <span>Upload Image</span>
                                </label>
                                {question.questionImage && (
                                  <img
                                    src={question.questionImage}
                                    alt="Question"
                                    className="h-16 w-16 object-cover rounded"
                                  />
                                )}
                              </div>
                            </div>

                            {/* MCQ Options */}
                            {question.questionType !== 'coding' && (
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                  Answer Options
                                </label>
                                <div className="space-y-2">
                                  {question.options.map((option, optionIndex) => (
                                    <div key={optionIndex} className="flex items-center space-x-2">
                                      <input
                                        type="checkbox"
                                        checked={option.isCorrect}
                                        onChange={(e) => updateQuestionOption(sectionIndex, questionIndex, optionIndex, 'isCorrect', e.target.checked)}
                                        className="rounded"
                                      />
                                      <span className="text-sm font-medium w-8">
                                        {String.fromCharCode(65 + optionIndex)}.
                                      </span>
                                      <input
                                        type="text"
                                        className="input-field flex-1"
                                        value={option.text}
                                        onChange={(e) => updateQuestionOption(sectionIndex, questionIndex, optionIndex, 'text', e.target.value)}
                                        placeholder={`Option ${String.fromCharCode(65 + optionIndex)}`}
                                      />
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}

                            {/* Coding Question Details */}
                            {question.questionType === 'coding' && (
                              <div className="space-y-4 border-t pt-4">
                                <h6 className="font-medium">Coding Problem Details</h6>
                                
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                  <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                      Input Format
                                    </label>
                                    <textarea
                                      rows="3"
                                      className="input-field"
                                      value={question.codingDetails.inputFormat}
                                      onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                        ...question.codingDetails,
                                        inputFormat: e.target.value
                                      })}
                                      placeholder="Describe input format"
                                    />
                                  </div>
                                  
                                  <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                      Output Format
                                    </label>
                                    <textarea
                                      rows="3"
                                      className="input-field"
                                      value={question.codingDetails.outputFormat}
                                      onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                        ...question.codingDetails,
                                        outputFormat: e.target.value
                                      })}
                                      placeholder="Describe output format"
                                    />
                                  </div>
                                </div>

                                <div>
                                  <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Constraints
                                  </label>
                                  <textarea
                                    rows="2"
                                    className="input-field"
                                    value={question.codingDetails.constraints}
                                    onChange={(e) => updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                      ...question.codingDetails,
                                      constraints: e.target.value
                                    })}
                                    placeholder="Enter constraints"
                                  />
                                </div>

                                {/* Examples */}
                                <div>
                                  <div className="flex justify-between items-center mb-2">
                                    <label className="block text-sm font-medium text-gray-700">
                                      Examples
                                    </label>
                                    <button
                                      type="button"
                                      onClick={() => addExample(sectionIndex, questionIndex)}
                                      className="text-primary-600 hover:text-primary-800 text-sm"
                                    >
                                      + Add Example
                                    </button>
                                  </div>
                                  
                                  {question.codingDetails.examples.map((example, exampleIndex) => (
                                    <div key={exampleIndex} className="bg-white p-3 rounded border mb-2">
                                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                        <div>
                                          <label className="block text-xs font-medium text-gray-600 mb-1">
                                            Input
                                          </label>
                                          <textarea
                                            rows="2"
                                            className="input-field text-sm"
                                            value={example.input}
                                            onChange={(e) => {
                                              const newExamples = [...question.codingDetails.examples];
                                              newExamples[exampleIndex].input = e.target.value;
                                              updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                                ...question.codingDetails,
                                                examples: newExamples
                                              });
                                            }}
                                          />
                                        </div>
                                        <div>
                                          <label className="block text-xs font-medium text-gray-600 mb-1">
                                            Output
                                          </label>
                                          <textarea
                                            rows="2"
                                            className="input-field text-sm"
                                            value={example.output}
                                            onChange={(e) => {
                                              const newExamples = [...question.codingDetails.examples];
                                              newExamples[exampleIndex].output = e.target.value;
                                              updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                                ...question.codingDetails,
                                                examples: newExamples
                                              });
                                            }}
                                          />
                                        </div>
                                      </div>
                                      <div className="mt-2">
                                        <label className="block text-xs font-medium text-gray-600 mb-1">
                                          Explanation (Optional)
                                        </label>
                                        <input
                                          type="text"
                                          className="input-field text-sm"
                                          value={example.explanation}
                                          onChange={(e) => {
                                            const newExamples = [...question.codingDetails.examples];
                                            newExamples[exampleIndex].explanation = e.target.value;
                                            updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                              ...question.codingDetails,
                                              examples: newExamples
                                            });
                                          }}
                                        />
                                      </div>
                                    </div>
                                  ))}
                                </div>

                                {/* Test Cases */}
                                <div>
                                  <div className="flex justify-between items-center mb-2">
                                    <label className="block text-sm font-medium text-gray-700">
                                      Test Cases
                                    </label>
                                    <button
                                      type="button"
                                      onClick={() => addTestCase(sectionIndex, questionIndex)}
                                      className="text-primary-600 hover:text-primary-800 text-sm"
                                    >
                                      + Add Test Case
                                    </button>
                                  </div>
                                  
                                  {question.codingDetails.testCases.map((testCase, testCaseIndex) => (
                                    <div key={testCaseIndex} className="bg-white p-3 rounded border mb-2">
                                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                        <div>
                                          <label className="block text-xs font-medium text-gray-600 mb-1">
                                            Input
                                          </label>
                                          <textarea
                                            rows="2"
                                            className="input-field text-sm"
                                            value={testCase.input}
                                            onChange={(e) => {
                                              const newTestCases = [...question.codingDetails.testCases];
                                              newTestCases[testCaseIndex].input = e.target.value;
                                              updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                                ...question.codingDetails,
                                                testCases: newTestCases
                                              });
                                            }}
                                          />
                                        </div>
                                        <div>
                                          <label className="block text-xs font-medium text-gray-600 mb-1">
                                            Expected Output
                                          </label>
                                          <textarea
                                            rows="2"
                                            className="input-field text-sm"
                                            value={testCase.output}
                                            onChange={(e) => {
                                              const newTestCases = [...question.codingDetails.testCases];
                                              newTestCases[testCaseIndex].output = e.target.value;
                                              updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                                ...question.codingDetails,
                                                testCases: newTestCases
                                              });
                                            }}
                                          />
                                        </div>
                                      </div>
                                      <div className="mt-2">
                                        <label className="flex items-center space-x-2">
                                          <input
                                            type="checkbox"
                                            checked={testCase.isHidden}
                                            onChange={(e) => {
                                              const newTestCases = [...question.codingDetails.testCases];
                                              newTestCases[testCaseIndex].isHidden = e.target.checked;
                                              updateQuestion(sectionIndex, questionIndex, 'codingDetails', {
                                                ...question.codingDetails,
                                                testCases: newTestCases
                                              });
                                            }}
                                            className="rounded"
                                          />
                                          <span className="text-xs text-gray-600">Hidden Test Case</span>
                                        </label>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateTest;
