import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';
import toast from 'react-hot-toast';
import { API_ENDPOINTS } from '../../config/api';
import { 
  ArrowLeft, 
  User,
  Mail,
  BookOpen,
  Award,
  Calendar
} from 'lucide-react';

const StudentProfile = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [testHistory, setTestHistory] = useState([]);

  useEffect(() => {
    fetchTestHistory();
  }, []);

  const fetchTestHistory = async () => {
    try {
      // First get all tests the student has attempted
      const response = await axios.get(API_ENDPOINTS.STUDENT_TESTS);
      const completedTests = response.data.tests.filter(test => 
        test.hasAttempted && test.isCompleted
      ).map(test => ({
        ...test,
        attemptDate: test.endDate,
        score: test.score || 0,
        maxScore: test.maxScore || 0,
        percentage: test.percentage || 0
      }));

      setTestHistory(completedTests);
    } catch (error) {
      console.error('Error fetching test history:', error);
      toast.error('Failed to load test history');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <Link
                to="/student"
                className="btn-secondary flex items-center space-x-2"
              >
                <ArrowLeft className="h-4 w-4" />
                <span>Back</span>
              </Link>
              <h1 className="text-2xl font-bold text-gray-900">Student Profile</h1>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Profile Information */}
          <div className="card">
            <div className="flex items-center space-x-4 mb-6">
              <div className="h-16 w-16 bg-primary-100 rounded-full flex items-center justify-center">
                <User className="h-8 w-8 text-primary-600" />
              </div>
              <div>
                <h2 className="text-xl font-semibold text-gray-900">{user.name}</h2>
                <p className="text-gray-600">{user.registrationNumber}</p>
              </div>
            </div>

            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-700">Email</label>
                <div className="mt-1 flex items-center space-x-2">
                  <Mail className="h-4 w-4 text-gray-400" />
                  <span>{user.email}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Test History */}
          <div className="md:col-span-2 card">
            <div className="flex items-center space-x-3 mb-6">
              <BookOpen className="h-6 w-6 text-primary-600" />
              <h2 className="text-xl font-semibold text-gray-900">Test History</h2>
            </div>

            {testHistory.length === 0 ? (
              <div className="text-center py-6">
                <Award className="mx-auto h-12 w-12 text-gray-400" />
                <h3 className="mt-2 text-sm font-medium text-gray-900">No tests completed yet</h3>
                <p className="mt-1 text-sm text-gray-500">Complete some tests to see your results here.</p>
              </div>
            ) : (
              <div className="space-y-4">
                {testHistory.map((test) => (
                  <div key={test._id} className="border border-gray-200 rounded-lg p-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="font-medium text-gray-900">{test.title}</h3>
                        <div className="mt-1 flex items-center space-x-4 text-sm text-gray-600">
                          <div className="flex items-center space-x-1">
                            <Calendar className="h-4 w-4" />
                            <span>{new Date(test.attemptDate).toLocaleDateString()}</span>
                          </div>
                          <div className="flex items-center space-x-1">
                            <Award className="h-4 w-4" />
                            <span>Score: {test.score}/{test.maxScore} ({test.percentage}%)</span>
                          </div>
                        </div>
                      </div>
                      <Link
                        to={`/student/test/${test._id}/result`}
                        className="btn-secondary text-sm"
                      >
                        View Result
                      </Link>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentProfile;
